                  <div className="space-y-1 mt-2">
                    {[
                      { href: "/diary", label: "My Diary", icon: BookOpen, testId: "nav-my-diary" },
                      { href: "/saved", label: "Saved Content", icon: Save, testId: "nav-saved-content" },
