                        </button>
                      );
                    })}
                  </div>
                )}
              </div>
            </nav>

            {/* Motivational Quote */}
            <div className="p-4">
              <MotivationalQuote />
            </div>
          </div>
        </div>

        {/* Main content area */}
        <div className="flex-1 flex flex-col overflow-hidden lg:ml-0">
          <Header sidebarOpen={sidebarOpen} setSidebarOpen={setSidebarOpen} />
          <main className="flex-1 overflow-auto">
            <Switch>
              <Route path="/" component={Dashboard} />
              <Route path="/chat" component={Chat} />
              <Route path="/screening" component={Screening} />
              <Route path="/games" component={Games} />
              <Route path="/music" component={MusicPage} />
              <Route path="/diary" component={DiaryPage} />
              <Route path="/videos" component={VideosPage} />
              <Route path="/resources" component={Resources} />
              <Route path="/community" component={Community} />
              <Route path="/peer-calling" component={PeerCalling} />
              <Route path="/showcase" component={Showcase} />
              <Route path="/doctor" component={Doctor} />
              <Route path="/medicine" component={Medicine} />
              <Route path="/entertainment" component={Entertainment} />
              <Route path="/live" component={Live} />
              <Route path="/yoga" component={YogaPage} />
              <Route path="/sleep" component={SleepPage} />
              <Route path="/routine" component={RoutinePage} />
              <Route path="/saved" component={SavedContent} />
              <Route path="/medicine-alarm" component={MedicineAlarmPage} />
              <Route path="/parent-portal" component={ParentPortal} />
              <Route path="/privacy" component={Privacy} />
              <Route path="/admin" component={Admin} />
              <Route path="/profile" component={Profile} />
              <Route component={NotFound} />
            </Switch>
          </main>
        </div>
      </div>

      <QuickActionsFAB />
      <OnboardingPopup 
        isOpen={isOnboarding} 
        onComplete={completeOnboarding}
      />
      <UserModePopup />
      <PageQuoteOverlay
        isVisible={showQuoteOverlay}
        onComplete={() => setShowQuoteOverlay(false)}
      />
    </>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <div className="min-h-screen bg-background">
          <AppProvider>
            <Router />
          </AppProvider>
        </div>
        <Toaster />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

