@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    scroll-behavior: smooth;
  }

  /* Custom scrollbar */
  ::-webkit-scrollbar {
    width: 8px;
    height: 8px;
  }

  ::-webkit-scrollbar-track {
    @apply bg-muted;
  }

  ::-webkit-scrollbar-thumb {
    background-color: hsl(var(--muted-foreground) / 0.3);
    border-radius: 0.375rem;
  }

  ::-webkit-scrollbar-thumb:hover {
    background-color: hsl(var(--muted-foreground) / 0.5);
  }

  /* Smooth transitions */
  .transition-colors {
    transition-property: color, background-color, border-color, text-decoration-color, fill, stroke;
    transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
    transition-duration: 150ms;
  }

  /* Focus styles */
  .focus-visible {
    @apply outline-none ring-2 ring-ring ring-offset-2 ring-offset-background;
  }

  /* Custom utilities & Soothing Gradients */
  .wellness-gradient {
    background: linear-gradient(135deg, hsl(var(--wellness)), hsl(var(--calm)));
  }

  .focus-gradient {
    background: linear-gradient(135deg, hsl(var(--focus)), hsl(var(--primary)));
  }

  .calm-gradient {
    background: linear-gradient(135deg, hsl(var(--calm)), hsl(var(--accent)));
  }

  .nature-gradient {
    background: linear-gradient(135deg, hsl(var(--nature)), hsl(var(--wellness)));
  }

  .healing-gradient {
    background: linear-gradient(135deg, hsl(var(--healing)), hsl(var(--hope)));
  }

  .hope-gradient {
    background: linear-gradient(135deg, hsl(var(--hope)), hsl(var(--calm)));
  }

  .meditation-gradient {
    background: linear-gradient(135deg, hsl(var(--accent)), hsl(var(--focus)));
  }

  /* Breathing animation for meditation */
  @keyframes breathe {
    0%, 100% { transform: scale(1); opacity: 0.7; }
    50% { transform: scale(1.05); opacity: 1; }
  }

  .animate-breathe {
    animation: breathe 4s ease-in-out infinite;
  }

  /* Gentle floating animation */
  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  /* Soft glow effect */
  .glow {
    box-shadow: 0 0 20px hsl(var(--primary) / 0.3);
  }

  .glow-wellness {
    box-shadow: 0 0 20px hsl(var(--wellness) / 0.4);
  }

  .glow-calm {
    box-shadow: 0 0 20px hsl(var(--calm) / 0.4);
  }

  /* Calm typography styles */
  .text-calm {
    color: hsl(var(--calm-foreground));
  }

  .text-wellness {
    color: hsl(var(--wellness));
  }

  .text-healing {
    color: hsl(var(--healing));
  }

  .text-nature {
    color: hsl(var(--nature));
  }

  .text-hope {
    color: hsl(var(--hope));
  }

  /* Background patterns */
  .pattern-dots {
    background-image: radial-gradient(circle, hsl(var(--muted-foreground) / 0.1) 1px, transparent 1px);
    background-size: 20px 20px;
  }

  .pattern-waves {
    background-image: url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='hsl(var(--muted-foreground) / 0.05)' fill-opacity='0.1'%3E%3Cpath d='M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E");
  }

  /* Profile emoji circle animation */
  @keyframes profile-orbit {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }
}

