      <CardContent className="flex-1 flex flex-col p-0">
        {/* Messages */}
        <div className="flex-1 overflow-y-auto overflow-x-hidden p-3 sm:p-6 space-y-3 sm:space-y-4 bg-gradient-to-b from-muted/20 to-background max-h-[500px] scroll-smooth">
          {chatMessages.map((message, index) => (
            <div
              key={index}
              className={`flex space-x-2 sm:space-x-3 ${message.role === "user" ? "justify-end" : ""}`}
              data-testid={`message-${index}`}
            >
              {message.role === "assistant" && (
                <div className="w-10 h-10 sm:w-12 sm:h-12 lg:w-14 lg:h-14 xl:w-16 xl:h-16 rounded-full flex-shrink-0 shadow-xl border-3 sm:border-4 border-primary/30 overflow-hidden bg-primary/5">
                  {chatType === "live" && selectedUser ? (
                    <div className="w-full h-full bg-gradient-to-br from-accent/20 to-primary/20 dark:from-accent/30 dark:to-primary/30 flex items-center justify-center">
                      <User className="h-5 w-5 sm:h-6 sm:w-6 lg:h-7 lg:w-7 xl:h-8 xl:w-8 text-primary dark:text-primary-foreground" />
                    </div>
                  ) : (
                    <img 
                      src={getAiAvatar(index)}
                      alt="AI Assistant"
                      className="w-full h-full object-cover"
                    />
                  )}
                </div>
              )}

              <div
                className={`max-w-[75%] sm:max-w-md lg:max-w-xl xl:max-w-2xl p-4 sm:p-5 lg:p-6 xl:p-7 rounded-3xl shadow-md border-3 sm:border-4 lg:border-5 transition-all hover:shadow-xl hover:scale-[1.02] word-wrap break-words overflow-hidden ${
                  message.role === "user"
                    ? "bg-primary text-primary-foreground rounded-br-lg border-primary/30"
                    : "bg-card border-border/50 rounded-bl-lg backdrop-blur-sm"
                }`}
              >
                <p className="text-sm sm:text-base lg:text-lg whitespace-pre-wrap leading-relaxed font-mono break-words">{message.content}</p>
                <p className="text-xs sm:text-sm opacity-60 mt-3 font-sans">
                  {message.timestamp ? new Date(message.timestamp).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'}) : ''}
                </p>
              </div>

              {message.role === "user" && (
                <div className="w-10 h-10 sm:w-12 sm:h-12 lg:w-14 lg:h-14 xl:w-16 xl:h-16 bg-accent rounded-full flex items-center justify-center flex-shrink-0 shadow-xl border-3 sm:border-4 border-accent/30">
                  <User className="text-accent-foreground h-5 w-5 sm:h-6 sm:w-6 lg:h-7 lg:w-7 xl:h-8 xl:w-8" />
                </div>
              )}
            </div>
          ))}

          {isTyping && (
            <div className="flex space-x-2 sm:space-x-3 animate-in slide-in-from-left-2 duration-300">
              <div className="w-8 h-8 sm:w-10 sm:h-10 lg:w-12 lg:h-12 rounded-full flex-shrink-0 shadow-md border-2 sm:border-3 border-primary/30 overflow-hidden bg-primary/5">
                {chatType === "live" && selectedUser ? (
                  <div className="w-full h-full bg-gradient-to-br from-accent/20 to-primary/20 dark:from-accent/30 dark:to-primary/30 flex items-center justify-center">
                    <User className="h-4 w-4 sm:h-5 sm:w-5 lg:h-6 lg:w-6 text-primary dark:text-primary-foreground" />
                  </div>
                ) : (
                  <img 
                    src={getAiAvatar(chatMessages.length)}
                    alt="AI Assistant"
                    className="w-full h-full object-cover"
                  />
