import { useState, useEffect } from "react";
import { Brain, Mail, Lock, Eye, EyeOff, Users } from "lucide-react";
import { FaGoogle } from "react-icons/fa";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useAppContext } from "@/context/AppContext";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import logoUrl from '@assets/logo (1)_1758315813528.png';

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const { login, isAuthenticated, isOnboarding } = useAppContext();
  const { toast } = useToast();
  const [, setLocation] = useLocation();

  // Redirect to dashboard when user is authenticated and not in onboarding
  useEffect(() => {
    if (isAuthenticated && !isOnboarding) {
      setLocation("/");
    }
  }, [isAuthenticated, isOnboarding, setLocation]);

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!email || !password) {
      toast({
        title: "Missing information",
        description: "Please enter both email and password",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);

    // Simulate fake login - accept any email/password
    try {
      await new Promise(resolve => setTimeout(resolve, 1000)); // Simulate API call
      login(email); // This will trigger the onboarding flow
      toast({
        title: "Welcome!",
        description: "Please complete your profile setup",
      });
    } catch (error) {
      toast({
        title: "Login failed",
        description: "Please try again",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };


  const handleGoogleLogin = async () => {
    setIsLoading(true);

    try {
      // Simulate Google OAuth flow
      await new Promise(resolve => setTimeout(resolve, 1500)); // Simulate OAuth redirect time

      // Simulate successful Google login with fake user data
      const fakeGoogleEmail = "user@gmail.com";
      login(fakeGoogleEmail);

      toast({
        title: "Welcome!",
        description: "Successfully signed in with Google. Please complete your profile setup",
      });
    } catch (error) {
      toast({
        title: "Google Sign-in failed",
        description: "Please try again",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="h-[100svh] bg-gradient-to-br from-primary/10 via-background to-secondary/20 flex items-center justify-center p-3">
      <Card className="w-full max-w-sm sm:max-w-md lg:max-w-lg flex flex-col bg-white/95 dark:bg-gray-900/95 backdrop-blur-xl border-2 border-gray-200/50 dark:border-gray-700/50 shadow-[0_8px_40px_rgba(0,0,0,0.12)] dark:shadow-[0_8px_40px_rgba(0,0,0,0.4)] rounded-2xl">
        <CardHeader className="py-2 sm:py-3 text-center flex-shrink-0">
          <div className="mx-auto mb-1 sm:mb-2 w-8 h-8 sm:w-10 sm:h-10 rounded-full flex items-center justify-center">
            <img src={logoUrl} alt="ApnaMann Logo" className="w-8 h-8 sm:w-10 sm:h-10 rounded-full object-cover" data-testid="img-logo-login" />
          </div>
          <CardTitle className="text-sm sm:text-base font-bold text-foreground">ApnaMann</CardTitle>
          <p className="text-xs sm:text-sm text-muted-foreground leading-tight">Mental Health Support â€¢ Sign in to access your platform</p>
        </CardHeader>

        <CardContent className="p-3 sm:p-4 flex-1">
          <div className="space-y-3 sm:space-y-4">
            {/* Main Login Form */}
            <div className="space-y-4">
              <form onSubmit={handleLogin} className="space-y-3">
                <div className="space-y-2">
                  <Label htmlFor="email" className="text-sm font-medium text-foreground">Email</Label>
                  <div className="relative">
                    <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400 dark:text-gray-500" />
                    <Input
                      id="email"
                      type="email"
                      placeholder="your.email@student.edu"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      className="pl-10 h-9 text-sm bg-white dark:bg-gray-800 border border-gray-300 dark:border-gray-600 hover:border-gray-400 dark:hover:border-gray-500 focus:border-blue-500 dark:focus:border-blue-400 focus:outline-none focus:ring-1 focus:ring-blue-500/20 dark:focus:ring-blue-400/20 text-foreground placeholder:text-muted-foreground transition-all duration-200 rounded-md"
                      data-testid="input-email"
                    />
                  </div>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="password" className="text-sm font-medium text-foreground">Password</Label>
                  <div className="relative">
                    <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400 dark:text-gray-500" />
                    <Input
                      id="password"
                      type={showPassword ? "text" : "password"}
                      placeholder="Enter password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      className="pl-10 pr-10 h-9 text-sm bg-white dark:bg-gray-800 border border-gray-300 dark:border-gray-600 hover:border-gray-400 dark:hover:border-gray-500 focus:border-blue-500 dark:focus:border-blue-400 focus:outline-none focus:ring-1 focus:ring-blue-500/20 dark:focus:ring-blue-400/20 text-foreground placeholder:text-muted-foreground transition-all duration-200 rounded-md"
                      data-testid="input-password"
                    />
                    <Button
                      type="button"
                      variant="ghost"
                      size="sm"
                      className="absolute right-1 top-1/2 transform -translate-y-1/2 h-6 w-6 p-0 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-md transition-colors duration-200"
                      onClick={() => setShowPassword(!showPassword)}
                      data-testid="button-toggle-password"
                    >
                      {showPassword ? (
                        <EyeOff className="h-3 w-3" />
                      ) : (
                        <Eye className="h-3 w-3" />
                      )}
                    </Button>
                  </div>
                </div>

                <div className="grid grid-cols-1 sm:grid-cols-3 gap-3 sm:gap-2" data-testid="row-auth-buttons">
                  <Button
                    type="submit"
                    className="h-10 text-sm font-semibold bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 hover:scale-105 active:scale-95 text-white shadow-lg hover:shadow-blue-500/30 hover:shadow-xl transition-all duration-300 rounded-lg"
                    disabled={isLoading}
                    data-testid="button-signin"
                  >
                    {isLoading ? "Signing up..." : "Sign Up"}
                  </Button>
                  <Button
                    type="button"
                    className="h-10 text-sm font-semibold bg-gradient-to-r from-emerald-600 to-emerald-700 hover:from-emerald-700 hover:to-emerald-800 hover:scale-105 active:scale-95 text-white shadow-lg hover:shadow-emerald-500/30 hover:shadow-xl transition-all duration-300 rounded-lg"
                    onClick={handleLogin}
                    disabled={isLoading}
                    data-testid="button-login"
                  >
                    {isLoading ? "Logging in..." : "Login"}
                  </Button>
                  <Button
                    variant="outline"
                    className="h-10 text-sm font-semibold bg-white dark:bg-gray-800 border-2 border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-700 hover:border-gray-400 dark:hover:border-gray-500 hover:scale-105 active:scale-95 text-gray-700 dark:text-gray-200 shadow-lg hover:shadow-gray-400/20 hover:shadow-xl transition-all duration-300 rounded-lg"
                    onClick={handleGoogleLogin}
                    disabled={isLoading}
                    data-testid="button-google-login"
                  >
                    <FaGoogle className="h-4 w-4 mr-2 text-red-500" />
                    <span className="hidden sm:inline">Google</span>
                    <span className="sm:hidden">Sign in with Google</span>
                  </Button>
                </div>
              </form>
            </div>

            {/* Additional Info Sections */}
            <div className="space-y-2 sm:space-y-3">
              <div className="text-center text-xs sm:text-sm text-teal-700 dark:text-teal-300 p-2 sm:p-3 bg-gradient-to-br from-teal-50 to-teal-100/50 dark:from-teal-950/50 dark:to-teal-900/30 backdrop-blur-sm rounded-lg border border-teal-200 dark:border-teal-800">
                <p className="font-medium mb-0.5 sm:mb-1 text-teal-800 dark:text-teal-200">New User?</p>
                <p className="leading-tight">Contact student services for account setup</p>
              </div>

              <div 
                className="p-2 sm:p-3 bg-gradient-to-br from-emerald-50 to-emerald-100/50 dark:from-emerald-950/50 dark:to-emerald-900/30 backdrop-blur-sm rounded-lg text-xs sm:text-sm border border-emerald-200 dark:border-emerald-800 text-center cursor-pointer hover:from-emerald-100 hover:to-emerald-200 hover:border-emerald-300 dark:hover:from-emerald-900/70 dark:hover:to-emerald-800/50 dark:hover:border-emerald-700 transition-all duration-200 hover:scale-[1.02] active:scale-[0.98]"
                onClick={() => window.open('/privacy', '_blank', 'noopener,noreferrer')}
                data-testid="div-privacy-security"
              >
                <h4 className="font-medium text-emerald-800 dark:text-emerald-200 mb-0.5 sm:mb-1 flex items-center justify-center">
                  <Lock className="h-3 w-3 mr-1 text-emerald-600 dark:text-emerald-400" />
                  Privacy & Security
                </h4>
                <p className="text-emerald-700 dark:text-emerald-300 leading-tight">
                  <span className="font-bold text-emerald-800 dark:text-emerald-200">Nobody knows details - your parents, institute, or no one.</span>
                </p>
                <p className="text-xs text-emerald-700 dark:text-emerald-300 mt-0.5 sm:mt-1 font-medium">Click for full details â†’</p>
              </div>

              <div className="p-2 sm:p-3 bg-gradient-to-br from-orange-50 to-orange-100/50 dark:from-orange-950/50 dark:to-orange-900/30 backdrop-blur-sm rounded-lg text-xs sm:text-sm border border-orange-200 dark:border-orange-800">
                <div className="text-center">
                  <Users className="h-4 w-4 sm:h-5 sm:w-5 mx-auto text-orange-600 dark:text-orange-400 mb-0.5 sm:mb-1" />
                  <h4 className="font-medium text-orange-800 dark:text-orange-200 mb-0.5 sm:mb-1">Parent Guide</h4>
                  <p className="text-orange-700 dark:text-orange-300 mb-1 sm:mb-2 leading-tight">
                    <span className="font-bold text-orange-800 dark:text-orange-200">Register as a student parent</span>
                  </p>
                  <Button
                    variant="outline"
                    size="sm"
                    className="w-full h-7 sm:h-8 text-xs bg-gradient-to-r from-orange-50 to-orange-100 dark:from-orange-900/50 dark:to-orange-800/30 border-orange-200 dark:border-orange-700 hover:bg-orange-100 dark:hover:bg-orange-800/40 hover:scale-105 active:scale-95 text-orange-700 dark:text-orange-300 font-medium shadow-md hover:shadow-orange-400/20 hover:shadow-lg transition-all duration-200"
                    onClick={() => setLocation("/parent-portal")}
                    data-testid="button-parent-portal"
                  >
                    Access Portal
                  </Button>
                </div>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/profile.tsx.content:
import { useState } from "react";
import { User, Settings, Bell, Shield, Languages, Palette, Download, Save, Camera } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { useAppContext, type ThemeType } from "@/context/AppContext";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { BackButton } from "@/components/ui/back-button";
import { getAnonymousDisplayName } from "@/utils/nameGenerator";

export default function Profile() {
  const { currentUser, setCurrentUser, theme, setTheme, toggleTheme } = useAppContext();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [formData, setFormData] = useState({
    firstName: currentUser?.firstName || "",
    lastName: currentUser?.lastName || "",
    email: currentUser?.email || "",
    institution: currentUser?.institution || "",
    course: currentUser?.course || "",
    year: currentUser?.year || 1,
    bio: "",
    language: currentUser?.language || "en",
  });

  const [notifications, setNotifications] = useState({
    appointmentReminders: true,
    screeningReminders: true,
    communityUpdates: false,
    crisisAlerts: true,
    weeklyReports: true,
  });

  const [privacy, setPrivacy] = useState({
    profileVisibility: "private",
    shareScreeningData: false,
    allowDirectMessages: true,
    showOnlineStatus: false,
  });

  // Update profile mutation
  const updateProfileMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      return await apiRequest("PATCH", `/api/users/${currentUser?.id}`, data);
    },
    onSuccess: (response) => {
      if (currentUser) {
        setCurrentUser({ ...currentUser, ...formData });
      }
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Profile updated",
        description: "Your profile has been successfully updated.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update profile. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Update notification preferences
  const updateNotificationsMutation = useMutation({
    mutationFn: async (data: typeof notifications) => {
      return await apiRequest("PATCH", `/api/users/${currentUser?.id}/notifications`, data);
    },
    onSuccess: () => {
      toast({
        title: "Notification preferences updated",
        description: "Your notification settings have been saved.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update notification preferences.",
        variant: "destructive",
      });
    },
  });

  // Update privacy settings
  const updatePrivacyMutation = useMutation({
    mutationFn: async (data: typeof privacy) => {
      return await apiRequest("PATCH", `/api/users/${currentUser?.id}/privacy`, data);
    },
    onSuccess: () => {
      toast({
        title: "Privacy settings updated",
        description: "Your privacy preferences have been saved.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update privacy settings.",
        variant: "destructive",
      });
    },
  });

  // Export user data
  const exportDataMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest("GET", `/api/users/${currentUser?.id}/export`, {});
    },
    onSuccess: (response) => {
      // Create download link
      const blob = new Blob([JSON.stringify(response, null, 2)], { type: 'application/json' });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `mental-health-data-${new Date().toISOString().split('T')[0]}.json`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);

      toast({
        title: "Data exported",
        description: "Your data has been downloaded successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to export data. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleInputChange = (field: string, value: any) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleNotificationChange = (field: string, value: boolean) => {
    setNotifications(prev => ({ ...prev, [field]: value }));
  };

  const handlePrivacyChange = (field: string, value: any) => {
    setPrivacy(prev => ({ ...prev, [field]: value }));
  };

  const handleSaveProfile = () => {
    updateProfileMutation.mutate(formData);
  };

  const handleSaveNotifications = () => {
    updateNotificationsMutation.mutate(notifications);
  };

  const handleSavePrivacy = () => {
    updatePrivacyMutation.mutate(privacy);
  };

  return (
    <main className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <BackButton />
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-foreground mb-2">Profile & Settings</h1>
        <p className="text-muted-foreground">
          Manage your account settings, privacy preferences, and notification options.
        </p>
      </div>

      <Tabs defaultValue="profile" className="space-y-6">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="profile" data-testid="tab-profile">
            <User className="h-4 w-4 mr-2" />
            Profile
          </TabsTrigger>
          <TabsTrigger value="notifications" data-testid="tab-notifications">
            <Bell className="h-4 w-4 mr-2" />
            Notifications
          </TabsTrigger>
          <TabsTrigger value="privacy" data-testid="tab-privacy">
            <Shield className="h-4 w-4 mr-2" />
            Privacy
          </TabsTrigger>
          <TabsTrigger value="preferences" data-testid="tab-preferences">
            <Settings className="h-4 w-4 mr-2" />
            Preferences
          </TabsTrigger>
        </TabsList>

        {/* Profile Tab */}
        <TabsContent value="profile">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <User className="h-5 w-5 mr-2" />
                Personal Information
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              {/* Profile Picture */}
              <div className="flex items-center space-x-4">
                <div className="w-20 h-20 bg-accent rounded-full flex items-center justify-center">
                  <span className="text-accent-foreground text-xl font-medium">
                    {formData.firstName?.[0]}{formData.lastName?.[0]}
                  </span>
                </div>
                <Button variant="outline" size="sm">
                  <Camera className="h-4 w-4 mr-2" />
                  Change Photo
                </Button>
              </div>

              <Separator />

              {/* Anonymous Display Name */}
              <div className="bg-accent/20 border border-accent rounded-lg p-4">
                <div className="flex items-center justify-between">
                  <div className="space-y-1">
                    <Label className="text-sm font-medium">Your Anonymous Name</Label>
                    <p className="text-2xl font-bold text-primary" data-testid="text-anonymous-name">
                      {getAnonymousDisplayName(currentUser || {})}
                    </p>
                    <p className="text-xs text-muted-foreground">
                      This unique anonymous name represents you in community discussions and helps protect your privacy.
                    </p>
                  </div>
                  <Badge variant="secondary" className="text-xs">Private</Badge>
                </div>
              </div>

              <Separator />

              {/* Basic Information */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="firstName">First Name</Label>
                  <Input
                    id="firstName"
                    value={formData.firstName}
                    onChange={(e) => handleInputChange("firstName", e.target.value)}
                    data-testid="input-first-name"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="lastName">Last Name</Label>
                  <Input
                    id="lastName"
                    value={formData.lastName}
                    onChange={(e) => handleInputChange("lastName", e.target.value)}
                    data-testid="input-last-name"
                  />
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="email">Email Address</Label>
                <Input
                  id="email"
                  type="email"
                  value={formData.email}
                  onChange={(e) => handleInputChange("email", e.target.value)}
                  data-testid="input-email"
                />
              </div>

              {/* Academic Information */}
              <Separator />
              <h3 className="text-lg font-medium">Academic Information</h3>

              <div className="space-y-2">
                <Label htmlFor="institution">Institution</Label>
                <Input
                  id="institution"
                  value={formData.institution}
                  onChange={(e) => handleInputChange("institution", e.target.value)}
                  data-testid="input-institution"
                />
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="course">Course/Major</Label>
                  <Input
                    id="course"
                    value={formData.course}
                    onChange={(e) => handleInputChange("course", e.target.value)}
                    data-testid="input-course"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="year">Year of Study</Label>
                  <Select value={formData.year.toString()} onValueChange={(value) => handleInputChange("year", parseInt(value))}>
                    <SelectTrigger data-testid="select-year">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="1">1st Year</SelectItem>
                      <SelectItem value="2">2nd Year</SelectItem>
                      <SelectItem value="3">3rd Year</SelectItem>
                      <SelectItem value="4">4th Year</SelectItem>
                      <SelectItem value="5">5th Year</SelectItem>
                      <SelectItem value="6">Graduate</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="bio">Bio (Optional)</Label>
                <Textarea
                  id="bio"
                  placeholder="Tell us a bit about yourself..."
                  value={formData.bio}
                  onChange={(e) => handleInputChange("bio", e.target.value)}
