const sleepQuestions: SleepQuestion[] = [
  {
    id: "current-bedtime",
    question: "What time do you usually go to bed?",
    options: [
      { value: "early", label: "9:00 PM - 10:30 PM" },
      { value: "normal", label: "10:30 PM - 12:00 AM" },
