    options: [
      { value: "very-early", label: "5:00 AM - 6:30 AM" },
      { value: "early", label: "6:30 AM - 8:00 AM" },
      { value: "normal", label: "8:00 AM - 9:30 AM" },
      { value: "late", label: "After 9:30 AM" }
    ]
  },
  {
    id: "sleep-quality",
    question: "How would you rate your current sleep quality?",
