import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { 
  CreditCard, 
  Lock, 
  Shield, 
  CheckCircle,
  AlertCircle,
  Loader2 
} from "lucide-react";

interface PaymentDetails {
  doctorName: string;
  consultationType: string;
  date: string;
  time: string;
  amount: number;
  currency: string;
}

interface ConsultationPaymentProps {
  paymentDetails: PaymentDetails;
  onPaymentSuccess: (paymentIntentId: string) => void;
  onPaymentCancel: () => void;
}

interface PaymentFormProps {
  paymentDetails: PaymentDetails;
  onPaymentSuccess: (paymentIntentId: string) => void;
  onPaymentCancel: () => void;
}

const PaymentForm = ({ paymentDetails, onPaymentSuccess, onPaymentCancel }: PaymentFormProps) => {
  const { toast } = useToast();
  const [isProcessing, setIsProcessing] = useState(false);
  const [paymentStatus, setPaymentStatus] = useState<"idle" | "processing" | "succeeded" | "failed">("idle");
  const [cardDetails, setCardDetails] = useState({
    cardNumber: "",
    expiryDate: "",
    cvv: "",
    cardHolder: ""
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Basic validation for demo
    if (!cardDetails.cardNumber || !cardDetails.expiryDate || !cardDetails.cvv || !cardDetails.cardHolder) {
      toast({
        title: "Payment Error",
        description: "Please fill in all payment details.",
        variant: "destructive",
      });
      return;
    }

    setIsProcessing(true);
    setPaymentStatus("processing");

    // Simulate payment processing for frontend-only implementation
    try {
      // Simulate processing time
      await new Promise(resolve => setTimeout(resolve, 3000));

      // Simulate successful payment
      const mockPaymentIntentId = `pi_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

      setPaymentStatus("succeeded");
      toast({
        title: "Payment Successful",
        description: "Your consultation has been booked successfully!",
      });

      setTimeout(() => {
        onPaymentSuccess(mockPaymentIntentId);
      }, 1500);

    } catch (error) {
      setPaymentStatus("failed");
      toast({
        title: "Payment Failed",
        description: "There was an error processing your payment. Please try again.",
        variant: "destructive",
      });
      setIsProcessing(false);
    }
  };

  if (paymentStatus === "succeeded") {
    return (
      <div className="text-center py-8">
        <CheckCircle className="h-16 w-16 text-green-500 mx-auto mb-4" />
        <h3 className="text-lg font-semibold mb-2">Payment Successful!</h3>
        <p className="text-muted-foreground">Your consultation has been booked successfully.</p>
      </div>
    );
  }

  if (paymentStatus === "failed") {
    return (
      <div className="text-center py-8">
        <AlertCircle className="h-16 w-16 text-destructive mx-auto mb-4" />
        <h3 className="text-lg font-semibold mb-2">Payment Failed</h3>
        <p className="text-muted-foreground mb-4">There was an error processing your payment.</p>
        <Button onClick={() => setPaymentStatus("idle")} variant="outline">
          Try Again
        </Button>
      </div>
    );
  }

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      {/* Payment Method Section */}
      <div className="space-y-4">
        <div className="flex items-center gap-2">
          <CreditCard className="h-5 w-5" />
          <h3 className="text-lg font-semibold">Payment Information</h3>
        </div>

        {/* Payment Form Fields */}
        <div className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="cardHolder">Cardholder Name</Label>
            <Input
              id="cardHolder"
              placeholder="John Doe"
              value={cardDetails.cardHolder}
              onChange={(e) => setCardDetails(prev => ({ ...prev, cardHolder: e.target.value }))}
              data-testid="input-card-holder"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="cardNumber">Card Number</Label>
            <Input
              id="cardNumber"
              placeholder="1234 5678 9012 3456"
              value={cardDetails.cardNumber}
              onChange={(e) => {
                const value = e.target.value.replace(/\D/g, '').replace(/(\d{4})(?=\d)/g, '$1 ');
                if (value.length <= 19) {
                  setCardDetails(prev => ({ ...prev, cardNumber: value }));
                }
              }}
              data-testid="input-card-number"
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="expiryDate">Expiry Date</Label>
              <Input
                id="expiryDate"
                placeholder="MM/YY"
                value={cardDetails.expiryDate}
                onChange={(e) => {
                  const value = e.target.value.replace(/\D/g, '').replace(/(\d{2})(?=\d)/, '$1/');
                  if (value.length <= 5) {
                    setCardDetails(prev => ({ ...prev, expiryDate: value }));
                  }
                }}
                data-testid="input-expiry-date"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="cvv">CVV</Label>
              <Input
                id="cvv"
                placeholder="123"
                value={cardDetails.cvv}
                onChange={(e) => {
                  const value = e.target.value.replace(/\D/g, '');
                  if (value.length <= 3) {
                    setCardDetails(prev => ({ ...prev, cvv: value }));
                  }
                }}
                data-testid="input-cvv"
              />
            </div>
          </div>

          <div className="text-xs text-muted-foreground flex items-center gap-1 mt-4">
            <Shield className="h-3 w-3" />
            This is a demo payment form. No actual payment will be processed.
          </div>
        </div>
      </div>

      {/* Security Notice */}
      <div className="flex items-center gap-2 p-3 bg-green-50 dark:bg-green-950/20 rounded-lg border border-green-200 dark:border-green-800">
        <Lock className="h-4 w-4 text-green-600" />
        <span className="text-sm text-green-700 dark:text-green-300">
          Your payment information is secured with 256-bit SSL encryption
        </span>
      </div>

      {/* Action Buttons */}
      <div className="flex gap-3 pt-4">
        <Button 
          type="button"
          variant="outline" 
          onClick={onPaymentCancel}
          className="flex-1"
          disabled={isProcessing}
          data-testid="button-cancel-payment"
        >
          Cancel
        </Button>
        <Button 
          type="submit"
          className="flex-1"
          disabled={isProcessing}
          data-testid="button-confirm-payment"
        >
          {isProcessing ? (
            <>
              <Loader2 className="h-4 w-4 mr-2 animate-spin" />
              Processing...
            </>
          ) : (
            <>
              <Lock className="h-4 w-4 mr-2" />
              Pay â‚¹{paymentDetails.amount}
            </>
          )}
        </Button>
      </div>
    </form>
  );
};

export default function ConsultationPayment({ paymentDetails, onPaymentSuccess, onPaymentCancel }: ConsultationPaymentProps) {
  return (
    <div className="space-y-6">
      {/* Booking Summary */}
      <Card>
        <CardHeader>
          <CardTitle className="text-lg">Booking Summary</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="space-y-3">
            <div className="flex justify-between items-center">
              <span className="text-sm text-muted-foreground">Doctor</span>
              <span className="font-medium">{paymentDetails.doctorName}</span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-sm text-muted-foreground">Consultation Type</span>
              <Badge variant="secondary">{paymentDetails.consultationType}</Badge>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-sm text-muted-foreground">Date & Time</span>
              <span className="font-medium">{paymentDetails.date} at {paymentDetails.time}</span>
            </div>
            <Separator />
            <div className="flex justify-between items-center text-lg font-semibold">
              <span>Total Amount</span>
              <span className="text-primary">â‚¹{paymentDetails.amount}</span>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Payment Form */}
      <Card>
        <CardContent className="pt-6">
          <PaymentForm 
            paymentDetails={paymentDetails}
            onPaymentSuccess={onPaymentSuccess}
            onPaymentCancel={onPaymentCancel}
          />
        </CardContent>
      </Card>
    </div>
  );
}

server/services/ai-provider.ts.content:
/**
 * Standardized interface for all AI service providers in the mental health support application.
 * This interface ensures consistent functionality across external API services (OpenAI, Gemini)
 * and local AI implementations.
 */

// Core types for AI provider interface
export interface ChatMessage {
  role: "user" | "assistant" | "system";
  content: string;
  timestamp?: Date;
}

export interface PsychologicalResponse {
  message: string;
  supportiveActions: string[];
  riskLevel: "low" | "moderate" | "high";
  escalationRequired: boolean;
}

// Additional configuration types
export interface PersonalityConfig {
  customPrompt?: string;
  name?: string;
  traits?: Record<string, any>;
}

export interface CrisisAnalysisResult {
  isHighRisk: boolean;
  severity: "low" | "moderate" | "high";
  indicators?: string[];
  recommendedActions?: string[];
}

// Exercise types with proper union for extensibility
export type ExerciseType = "breathing" | "relaxation" | "mindfulness" | (string & {});

export interface IAIProvider {
  /**
   * Generate a psychological response based on conversation history
   * @param messages - Array of conversation messages for context
   * @param personality - Optional custom personality configuration
   * @returns Promise resolving to structured psychological response
   */
  generateResponse(messages: ChatMessage[], personality?: PersonalityConfig): Promise<PsychologicalResponse>;

  /**
   * Analyze a message for crisis indicators and risk assessment
   * Optional method - providers can implement this or delegate to a crisis service
   * @param message - User message to analyze
   * @returns Promise resolving to crisis analysis with risk level
   */
  analyzeForCrisis?(message: string): Promise<CrisisAnalysisResult>;

  /**
   * Generate step-by-step guided exercise instructions
   * Optional method - providers can implement this or delegate to a shared service
   * @param type - Type of exercise (breathing, relaxation, mindfulness, etc.)
   * @returns Promise resolving to array of exercise steps
   */
  generateGuidedExercise?(type: ExerciseType): Promise<string[]>;

  /**
   * Check if the AI provider is currently available and functional
   * @returns Promise resolving to boolean indicating availability
   */
  isAvailable(): Promise<boolean>;

  /**
   * Get the provider's identification string for logging/debugging
   * @returns Human-readable provider name
   */
  getProviderName(): string;
}

export interface IAIProviderConfig {
  timeoutMs?: number;
  maxRetries?: number;
  fallbackEnabled?: boolean;
}

server/services/local-ai-adapter.ts.content:
/**
 * LocalAIAdapter - Wraps LocalAIService to implement the IAIProvider interface
 * This adapter enables the LocalAIService to work with the OfflineFirstAIOrchestrator
 * while providing fallback implementations for optional methods.
 */

import { LocalAIService } from './local-ai';
import type { 
  IAIProvider, 
  ChatMessage, 
  PsychologicalResponse, 
  PersonalityConfig, 
  CrisisAnalysisResult, 
  ExerciseType 
} from './ai-provider';

export class LocalAIAdapter implements IAIProvider {
  private localAI: LocalAIService;

  constructor(chatData?: string) {
    this.localAI = new LocalAIService(chatData);
  }

  /**
   * Generate psychological response using LocalAIService
   */
  async generateResponse(messages: ChatMessage[], personality?: PersonalityConfig): Promise<PsychologicalResponse> {
    // Apply personality configuration if provided
    if (personality) {
      const localPersonality = {
        customPrompt: personality.customPrompt,
        name: personality.name,
        traits: personality.traits
      };
      this.localAI.setCustomPersonality(localPersonality);
    }

    // LocalAIService.generateResponse is synchronous, so we wrap in Promise.resolve
    const localResponse = this.localAI.generateResponse(messages);

    // The response structure already matches PsychologicalResponse interface
    return {
      message: localResponse.message,
      supportiveActions: localResponse.supportiveActions,
      riskLevel: localResponse.riskLevel,
      escalationRequired: localResponse.escalationRequired
    };
  }

  /**
   * Basic crisis analysis using simple keyword detection
   * This is a simplified implementation - in production, this could delegate to a more sophisticated crisis service
   */
  async analyzeForCrisis(message: string): Promise<CrisisAnalysisResult> {
    const messageLower = message.toLowerCase();

    // High-risk indicators
    const highRiskKeywords = [
      'suicide', 'kill myself', 'end my life', 'want to die', 'better off dead',
      'hurt myself', 'self harm', 'overdose', 'pills', 'jumping', 'hanging'
    ];

    // Moderate risk indicators
    const moderateRiskKeywords = [
      'depressed', 'hopeless', 'worthless', 'empty', 'alone forever',
      'can\'t go on', 'no point', 'give up', 'tired of living'
    ];

    const hasHighRisk = highRiskKeywords.some(keyword => messageLower.includes(keyword));
    const hasModerateRisk = moderateRiskKeywords.some(keyword => messageLower.includes(keyword));

    let severity: "low" | "moderate" | "high" = "low";
    let isHighRisk = false;
    let indicators: string[] = [];
    let recommendedActions: string[] = [];

    if (hasHighRisk) {
      severity = "high";
      isHighRisk = true;
      indicators = ['Direct expression of self-harm intent'];
      recommendedActions = [
        'Immediate professional intervention required',
        'Contact emergency services if necessary',
        'Do not leave the person alone'
      ];
    } else if (hasModerateRisk) {
      severity = "moderate";
      isHighRisk = false;
      indicators = ['Expressions of hopelessness or depression'];
      recommendedActions = [
        'Encourage professional support',
        'Check in regularly',
        'Provide crisis hotline information'
      ];
    } else {
      recommendedActions = ['Continue supportive conversation', 'Monitor for changes'];
    }

    return {
      isHighRisk,
      severity,
      indicators,
      recommendedActions
    };
  }

  /**
   * Generate guided exercise instructions
   * This provides basic exercises - could be enhanced with more sophisticated content
   */
  async generateGuidedExercise(type: ExerciseType): Promise<string[]> {
    switch (type) {
      case "breathing":
        return [
          "Find a comfortable position, sitting or lying down",
          "Close your eyes or soften your gaze",
          "Breathe in slowly through your nose for 4 counts",
          "Hold your breath gently for 4 counts", 
          "Exhale slowly through your mouth for 6 counts",
          "Repeat this cycle 5-10 times",
          "Notice how your body feels more relaxed with each breath"
        ];

      case "relaxation":
        return [
          "Lie down in a comfortable position",
          "Start by tensing the muscles in your toes for 5 seconds, then release",
          "Move up to your calves - tense for 5 seconds, then relax",
          "Continue with your thighs, buttocks, and abdomen",
          "Tense your arms, hands, and shoulders, then let go",
          "Finally, scrunch your face muscles, then relax completely",
          "Take a moment to notice the feeling of complete relaxation",
          "Breathe deeply and enjoy this peaceful state"
        ];

      case "mindfulness":
        return [
          "Sit comfortably with your feet flat on the ground",
          "Take three deep breaths to center yourself",
          "Notice 5 things you can see around you",
          "Identify 4 things you can physically feel (chair, clothes, temperature)",
          "Listen for 3 different sounds in your environment",
          "Find 2 things you can smell",
          "Think of 1 thing you can taste",
          "Take a final deep breath and return to the present moment"
        ];

      default:
        return [
          "Sit in a quiet, comfortable space",
          "Take a few deep breaths to center yourself",
          "Focus on the present moment",
          "Allow thoughts to come and go without judgment",
          "When ready, gently return your attention to your surroundings"
        ];
    }
  }

  /**
   * Check if LocalAI is available (always true for local service)
   */
  async isAvailable(): Promise<boolean> {
    return true;
  }

  /**
   * Get provider identification
   */
  getProviderName(): string {
    return "LocalAI";
  }

  /**
   * Allow learning from chat data for personality development
   */
  learnFromChat(chatContent: string): void {
    this.localAI.learnFromChat(chatContent);
  }
}

server/services/offline-first-ai-orchest.content:
/**
 * OfflineFirstAIOrchestrator - Intelligent AI service orchestration with offline-first design
 * 
 * This orchestrator prioritizes external AI services (OpenAI, Gemini) but gracefully falls back
 * to LocalAIService when external services are unavailable, slow, or rate-limited.
 * 
 * Features:
 * - Circuit breaker pattern for external services
 * - Configurable timeouts and retries
 * - Health monitoring and automatic recovery
 * - Seamless offline mode operation
 * - Intelligent provider selection
 */

import type { 
  IAIProvider, 
  ChatMessage, 
  PsychologicalResponse, 
  PersonalityConfig, 
  CrisisAnalysisResult, 
  ExerciseType,
  IAIProviderConfig
} from './ai-provider';
import { LocalAIAdapter } from './local-ai-adapter';

interface CircuitBreakerState {
  isOpen: boolean;
  failureCount: number;
  lastFailureTime: number;
  successCount: number;
}

interface ProviderHealth {
  isAvailable: boolean;
  lastCheck: number;
  responseTimeMs: number;
  circuitBreaker: CircuitBreakerState;
}

export interface OrchestratorConfig {
  // Timeout settings
  externalProviderTimeoutMs: number;
  localProviderTimeoutMs: number;

  // Circuit breaker settings
  circuitBreakerFailureThreshold: number;
  circuitBreakerRecoveryTimeMs: number;
  circuitBreakerHalfOpenRetryMs: number;

  // Health check settings
  healthCheckIntervalMs: number;
  healthCheckTimeoutMs: number;

  // Provider preferences
  preferredExternalProvider: 'primary' | 'secondary' | 'auto';
  forceOfflineMode: boolean;
  enableFallback: boolean;
}

export class OfflineFirstAIOrchestrator implements IAIProvider {
  private config: OrchestratorConfig;
  private localProvider: LocalAIAdapter;
  private externalProviders: Map<string, IAIProvider>;
  private providerHealth: Map<string, ProviderHealth>;
  private lastHealthCheck: number = 0;

  constructor(config: Partial<OrchestratorConfig> = {}, chatData?: string) {
    this.config = {
      externalProviderTimeoutMs: 8000,
      localProviderTimeoutMs: 2000,
      circuitBreakerFailureThreshold: 3,
      circuitBreakerRecoveryTimeMs: 60000, // 1 minute
      circuitBreakerHalfOpenRetryMs: 30000, // 30 seconds
      healthCheckIntervalMs: 30000, // 30 seconds
      healthCheckTimeoutMs: 5000,
      preferredExternalProvider: 'auto',
      forceOfflineMode: false,
      enableFallback: true,
      ...config
    };

    this.localProvider = new LocalAIAdapter(chatData);
    this.externalProviders = new Map();
    this.providerHealth = new Map();
  }

  /**
   * Register an external AI provider
   */
  registerExternalProvider(name: string, provider: IAIProvider): void {
    this.externalProviders.set(name, provider);
    this.providerHealth.set(name, {
      isAvailable: true,
      lastCheck: 0,
      responseTimeMs: 0,
      circuitBreaker: {
        isOpen: false,
        failureCount: 0,
        lastFailureTime: 0,
        successCount: 0
      }
    });
  }

  /**
   * Generate response with intelligent provider selection
   */
  async generateResponse(messages: ChatMessage[], personality?: PersonalityConfig): Promise<PsychologicalResponse> {
    const userMessage = messages[messages.length - 1]?.content || '';
    const isHealthcareQuery = this.isHealthcareRelated(userMessage);

    // Force offline mode if configured
    if (this.config.forceOfflineMode) {
      console.log('Offline mode forced, using LocalAI');

      // Try Python healthcare AI first for medical queries
      if (isHealthcareQuery) {
        const pythonProvider = this.externalProviders.get('python-healthcare');
        if (pythonProvider) {
          try {
            console.log('Attempting healthcare query with Python AI');
            return await this.executeWithTimeout(
              () => pythonProvider.generateResponse(messages, personality),
              15000, // 15 second timeout for Python AI
              'Python Healthcare AI'
            );
          } catch (error) {
            console.warn('Python Healthcare AI failed, falling back to LocalAI:', error);
          }
        }
      }

      return this.executeWithTimeout(
        () => this.localProvider.generateResponse(messages, personality),
        this.config.localProviderTimeoutMs,
        'LocalAI'
      );
    }

    // Check health and update circuit breakers
    await this.updateProviderHealth();

    // Try external providers first
    const availableProviders = this.getAvailableExternalProviders();

    for (const providerName of availableProviders) {
      try {
        console.log(`Attempting response generation with ${providerName}`);
        const provider = this.externalProviders.get(providerName);
        if (!provider) continue;

        const response = await this.executeWithTimeout(
          () => provider.generateResponse(messages, personality),
          this.config.externalProviderTimeoutMs,
          providerName
        );

        // Mark success
        this.recordProviderSuccess(providerName);
        console.log(`Successfully generated response with ${providerName}`);
        return response;

      } catch (error) {
        console.warn(`Provider ${providerName} failed:`, error);
        this.recordProviderFailure(providerName);
        continue; // Try next provider
      }
    }

    // Fallback to local provider
    if (this.config.enableFallback) {
      console.log('All external providers failed, falling back to LocalAI');

      // Try Python healthcare AI as additional fallback for medical queries
      if (isHealthcareQuery) {
        const pythonProvider = this.externalProviders.get('python-healthcare');
        if (pythonProvider) {
          try {
            console.log('Attempting healthcare fallback with Python AI');
            return await this.executeWithTimeout(
              () => pythonProvider.generateResponse(messages, personality),
              15000, // 15 second timeout for Python AI
              'Python Healthcare AI (fallback)'
            );
          } catch (error) {
            console.warn('Python Healthcare AI fallback failed:', error);
          }
        }
      }

      try {
        return await this.executeWithTimeout(
          () => this.localProvider.generateResponse(messages, personality),
          this.config.localProviderTimeoutMs,
          'LocalAI'
        );
      } catch (error) {
        console.error('LocalAI also failed:', error);
        // Return a basic fallback response
        return this.getEmergencyFallbackResponse();
      }
    }

    throw new Error('All AI providers are unavailable and fallback is disabled');
  }

  /**
   * Crisis analysis with provider selection
   */
  async analyzeForCrisis(message: string): Promise<CrisisAnalysisResult> {
    // Always try external providers first for crisis analysis as it's critical
    if (!this.config.forceOfflineMode) {
      const availableProviders = this.getAvailableExternalProviders();

      for (const providerName of availableProviders) {
        try {
          const provider = this.externalProviders.get(providerName);
          if (!provider?.analyzeForCrisis) continue;

          const result = await this.executeWithTimeout(
            () => provider.analyzeForCrisis!(message),
            this.config.externalProviderTimeoutMs,
            providerName
          );

          this.recordProviderSuccess(providerName);
          return result;

        } catch (error) {
          console.warn(`Crisis analysis failed with ${providerName}:`, error);
          this.recordProviderFailure(providerName);
          continue;
        }
      }
    }

    // Fallback to local analysis
    console.log('Using LocalAI for crisis analysis');
    return this.localProvider.analyzeForCrisis!(message);
  }

  /**
   * Guided exercise generation
   */
  async generateGuidedExercise(type: ExerciseType): Promise<string[]> {
    // Try external providers first
    if (!this.config.forceOfflineMode) {
      const availableProviders = this.getAvailableExternalProviders();

      for (const providerName of availableProviders) {
        try {
          const provider = this.externalProviders.get(providerName);
          if (!provider?.generateGuidedExercise) continue;

          const result = await this.executeWithTimeout(
            () => provider.generateGuidedExercise!(type),
            this.config.externalProviderTimeoutMs,
            providerName
          );

          this.recordProviderSuccess(providerName);
          return result;

        } catch (error) {
          console.warn(`Guided exercise generation failed with ${providerName}:`, error);
          this.recordProviderFailure(providerName);
          continue;
        }
      }
    }

    // Fallback to local generation
    console.log('Using LocalAI for guided exercise generation');
    return this.localProvider.generateGuidedExercise!(type);
  }

  /**
   * Check if orchestrator is available
   */
  async isAvailable(): Promise<boolean> {
    // If offline mode is forced, only check local provider
    if (this.config.forceOfflineMode) {
      return this.localProvider.isAvailable();
    }

    // Check if any provider is available
    const localAvailable = await this.localProvider.isAvailable();
    const externalAvailable = this.getAvailableExternalProviders().length > 0;

    return localAvailable || externalAvailable;
  }

  /**
   * Get provider identification
   */
  getProviderName(): string {
    if (this.config.forceOfflineMode) {
      return `Orchestrator (offline: ${this.localProvider.getProviderName()})`;
    }

    const availableExternal = this.getAvailableExternalProviders();
    if (availableExternal.length > 0) {
      return `Orchestrator (primary: ${availableExternal[0]}, fallback: ${this.localProvider.getProviderName()})`;
    }

    return `Orchestrator (offline: ${this.localProvider.getProviderName()})`;
  }

  /**
   * Execute function with timeout
   */
  private async executeWithTimeout<T>(
    fn: () => Promise<T>, 
    timeoutMs: number, 
    providerName: string
  ): Promise<T> {
    const startTime = Date.now();

    return Promise.race([
      fn(),
      new Promise<never>((_, reject) => 
        setTimeout(() => reject(new Error(`${providerName} timeout after ${timeoutMs}ms`)), timeoutMs)
      )
    ]).then(result => {
      const responseTime = Date.now() - startTime;
      this.updateProviderResponseTime(providerName, responseTime);
      return result;
    });
  }

  /**
   * Get available external providers based on circuit breaker status
   */
  private getAvailableExternalProviders(): string[] {
    const now = Date.now();
    return Array.from(this.externalProviders.keys()).filter(name => {
      const health = this.providerHealth.get(name);
      if (!health) return false;

      const breaker = health.circuitBreaker;

      // Circuit breaker is closed (working normally)
      if (!breaker.isOpen) return true;

      // Circuit breaker is open - check if recovery time has passed
      if (now - breaker.lastFailureTime > this.config.circuitBreakerRecoveryTimeMs) {
        // Allow half-open state for testing
        return true;
      }

      return false;
    });
  }

  /**
   * Update provider health status
   */
  private async updateProviderHealth(): Promise<void> {
    const now = Date.now();

    // Don't check too frequently
    if (now - this.lastHealthCheck < this.config.healthCheckIntervalMs) {
      return;
    }

    this.lastHealthCheck = now;

    // Check each external provider's health
    for (const [name, provider] of Array.from(this.externalProviders.entries())) {
      try {
        const startTime = Date.now();
        const available = await this.executeWithTimeout(
          () => provider.isAvailable(),
          this.config.healthCheckTimeoutMs,
          `${name} health check`
        ) as boolean;

        const responseTime = Date.now() - startTime;

        const health = this.providerHealth.get(name)!;
        health.isAvailable = available;
        health.lastCheck = now;
        health.responseTimeMs = responseTime;

      } catch (error) {
        const health = this.providerHealth.get(name)!;
        health.isAvailable = false;
        health.lastCheck = now;
        console.warn(`Health check failed for ${name}:`, error);
      }
    }
  }

  /**
   * Record provider success for circuit breaker
   */
  private recordProviderSuccess(providerName: string): void {
    const health = this.providerHealth.get(providerName);
    if (!health) return;

    const breaker = health.circuitBreaker;
    breaker.successCount++;
    breaker.failureCount = 0;

    // Close circuit breaker if it was open
    if (breaker.isOpen) {
      console.log(`Circuit breaker closed for ${providerName} after successful request`);
      breaker.isOpen = false;
    }
  }

  /**
   * Record provider failure for circuit breaker
   */
  private recordProviderFailure(providerName: string): void {
    const health = this.providerHealth.get(providerName);
    if (!health) return;

    const breaker = health.circuitBreaker;
    breaker.failureCount++;
    breaker.lastFailureTime = Date.now();
    breaker.successCount = 0;

    // Open circuit breaker if failure threshold reached
    if (breaker.failureCount >= this.config.circuitBreakerFailureThreshold && !breaker.isOpen) {
      console.warn(`Circuit breaker opened for ${providerName} after ${breaker.failureCount} failures`);
      breaker.isOpen = true;
    }
  }

  /**
   * Update provider response time metrics
   */
  private updateProviderResponseTime(providerName: string, responseTimeMs: number): void {
    const health = this.providerHealth.get(providerName);
    if (health) {
      health.responseTimeMs = responseTimeMs;
    }
  }

  /**
   * Emergency fallback response when all providers fail
   */
  private getEmergencyFallbackResponse(): PsychologicalResponse {
    return {
      message: "I'm having trouble connecting to my AI services right now, but I'm here to listen. " +
               "Please know that you're not alone, and if this is urgent, don't hesitate to reach out " +
               "to a counselor or crisis helpline immediately.",
      supportiveActions: [
        "Take deep breaths to stay calm",
        "Reach out to a trusted friend or family member", 
        "Contact your campus counseling center",
        "Call a crisis helpline if you're in immediate distress"
      ],
      riskLevel: "moderate",
      escalationRequired: true
    };
  }

  /**
   * Get current provider health status (for monitoring/debugging)
   */
  getProviderStatus(): Record<string, any> {
    const status: Record<string, any> = {
      config: {
        forceOfflineMode: this.config.forceOfflineMode,
        enableFallback: this.config.enableFallback
      },
      providers: {}
    };

    // Add local provider status
    status.providers.local = {
      name: this.localProvider.getProviderName(),
      isAvailable: true, // LocalAI is always available
      type: 'local'
    };

    // Add external provider status
    for (const [name, provider] of Array.from(this.externalProviders.entries())) {
      const health = this.providerHealth.get(name);
      status.providers[name] = {
        name: provider.getProviderName(),
        isAvailable: health?.isAvailable ?? false,
        responseTimeMs: health?.responseTimeMs ?? 0,
        circuitBreakerOpen: health?.circuitBreaker.isOpen ?? false,
        failureCount: health?.circuitBreaker.failureCount ?? 0,
        lastCheck: health?.lastCheck ?? 0,
        type: 'external'
      };
    }

    return status;
  }

  /**
   * Check if a message is healthcare-related
   */
  private isHealthcareRelated(message: string): boolean {
    const healthcareKeywords = [
      'health', 'medical', 'disease', 'symptom', 'treatment', 'medicine',
      'doctor', 'hospital', 'diagnosis', 'therapy', 'medication', 'drug',
      'pain', 'fever', 'infection', 'cancer', 'diabetes', 'heart',
      'blood', 'surgery', 'vaccine', 'virus', 'bacteria', 'wellness',
      'nutrition', 'diet', 'exercise', 'mental health', 'depression',
      'anxiety', 'stress', 'injury', 'emergency', 'first aid'
    ];

    const messageLower = message.toLowerCase();
    return healthcareKeywords.some(keyword => messageLower.includes(keyword));
  }

  /**
   * Allow learning from chat data for the local provider
   */
  learnFromChat(chatContent: string): void {
    this.localProvider.learnFromChat(chatContent);
  }
}

